mapper.insert:
INSERT INTO humidities (humidity_percentage, measured_at, sensor_id)
SELECT #{humidityPercentage}, #{measuredAt}, ss.id
FROM sensors ss
JOIN sections st ON ss.section_id = st.id AND ss.name = #{sensorName} AND st.name = #{sectionName}
JOIN crops c ON st.crop_id = c.id AND c.name = #{cropsName}
JOIN farms f ON c.farm_id = f.id AND f.uuid = #{farmUuid};

mapper.select:
SELECT *
FROM humidities
ORDER BY id DESC;
